INSERT INTO `IcerikHaber` (`No`, `Baslik`, `Icerik`, `HaberSlogan`, `Kaynak`, `Durum`, `Manset`, `Tarih`, `Resim`) VALUES (46, 'PARDUS Çalýþan CD Ýlk Sürümü Duyuruldu', '<img width="200" vspace="10" hspace="10" height="241" border="0" align="left" alt="Pardus 1.0 Çalýþan CD" src="/pardul/templates/resimler/Image/sistem/tasarim/pardus_logo_fff.png"/>Ulusal Daðýtým Projesi ilk &uuml;r&uuml;n&uuml;n&uuml; &ccedil;ýkardý:<br/>\r\nPardus Ulusal Ýþletim Sistemi &Ccedil;alýþan CD<br/>\r\n<br/>\r\n<span class="metin">Gaziantep &Uuml;niversitesi''nde d&uuml;zenlenen <a href="http://ab2005.gantep.edu.tr/index.php">Akademik Biliþim 2005</a> konferansýnda duyurulan ve katýlýmcýlara daðýtýlan Pardus &Ccedil;alýþan CD, Pardus Ulusal Ýþletim Sistemi''nin CD &uuml;zerinde &ccedil;alýþan s&uuml;r&uuml;m&uuml;n&uuml; i&ccedil;eriyor. CD i&ccedil;erisinde ofis ara&ccedil;larý (metin d&uuml;zenleyici, hesap tablosu, sunum hazýrlayýcý, vb.), Ýnternet ara&ccedil;larý (tarayýcý, e-posta, sohbet, vb.), &ccedil;okluortam ve grafik ara&ccedil;larý (film oynatýcý, m&uuml;zik &ccedil;alar, vb.), oyunlar ve &ccedil;ok sayýda uygulama mevcut. </span>\r\n<p class="metin"> Ýstanbul Bilgi &Uuml;niversitesinin d&uuml;zenlediði <a href="http://open.bilgi.edu.tr/freedays/">A&ccedil;ýk Kaynak G&uuml;nleri 2005</a> etkinliðinde, katýlýmcýlara Ulusal Daðýtým hakkýnda bilgi verildi ve <a href="urunler/pardus_calisan_cd_1.0.html">Pardus &Ccedil;alýþan CD 1.0</a> tam s&uuml;r&uuml;m&uuml; daðýtýldý.  </p>\r\n<p class="baslik"><span class="emph">Pardus, &ouml;zg&uuml;r yazýlým yaklaþýmý ile a&ccedil;ýk kaynaklý yayýmlanmakta ve</span> <a href="./gpl.html">GNU Genel Kamu Lisansý</a>              <span class="emph">(General Public License &ndash; GPL) ile &ouml;zg&uuml;rce daðýtýlmaktadýr. Bu karar 30 Ocak 2005''te yayýnlanan Uludað Haftalýk B&uuml;lteni''nde a&ccedil;ýklanmýþtý.</span></p>', 'Ulusal Daðýtým Projesi ilk &uuml;r&uuml;n&uuml;n&uuml; &ccedil;ýkardý:<br/>\r\nPardus Ulusal Ýþletim Sistemi &Ccedil;alýþan CD', '', 'Aktif', 'Aktif', '2005-05-03', '');
INSERT INTO `IcerikHaber` (`No`, `Baslik`, `Icerik`, `HaberSlogan`, `Kaynak`, `Durum`, `Manset`, `Tarih`, `Resim`) VALUES (47, 'Pardul 1001', '<span class="Title">PARDUL: Pardus Donaným Uyumluluðu Listesi</span>\r\n<p><img width="100" vspace="5" hspace="5" height="100" border="0" align="right" src="/pardul/templates/resimler/Image/sistem/tasarim/pardul_logo_100.png" alt="PARDUL"/>1001. donanýma ulaþan PARDUL artýk daha fonksiyonel. Kinfocenter''da Barýþ Metin tarafýndan yapýlan yeni d&uuml;zenlemeler ile donaným sorgulamalarý PARDUL Web Servisi aracýlýðý ile online yapýlabiliyor. Donanýmýn kullanýlan &ccedil;ekirdek ile uyumluluðu test edilirken, ayný zamanda tanýtýlmak istenen donanýma ait &ccedil;ekirdekte destek verilmesi gereken ayarlamalar otomatik olarak PARDUL''dan alýnarak yeni &ccedil;ekirdek veya mod&uuml;ller oluþturulabiliyor.</p>', 'PARDUL - Pardus Donaným Uyumluluðu Listesi 1001 dedi!<br/>', 'PARDUL - UEKAE', 'Aktif', 'Aktif', '2005-05-24', '');
